INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('5', '2', '公司简介', '<p>&nbsp;</p>\r\n<div style=\"text-indent: 21pt\">固杉精密夹具商城是一家专业从事夹具、检具及工装的销售、维修、维护与一体的综合型公司。</div>\r\n<div><span style=\"font-size: 9pt; color: black\">&nbsp;</span></div>\r\n<div style=\"text-indent: 21pt\">随着中国产业升级的步伐进一步加快,中国制造业正向着高质量高精度的方向快速发展, 对高品质高性能生产工具的需求也日益增加，公司根据客户的需求发展了不同类别、不同领域的各类专用夹具、检具。与广大厂商和同行建立了良好的合作关系，致力于为客户生产加工的效率提升做出不懈努力。</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>我公司经营理念：&ldquo;求实、诚信、互惠、双赢&rdquo;，服务宗旨：&ldquo;客户至上、品质第一&rdquo;</div>\r\n<div>&nbsp;</div>', '', '', '', '0', '1', '1331342345', '', '0', 'http://', '');
